import React, { useState, useEffect } from 'react';
import axios from 'axios';

export const MatchForm = () => {
  const [customers, setCustomers] = useState([]);
  const [selectedCustomers, setSelectedCustomers] = useState([]);

  useEffect(() => {
    const fetchCustomers = async () => {
      const { data } = await axios.get('/api/customers');
      setCustomers(data);
    };
    fetchCustomers();
  }, []);

  const handleSelect = (e) => {
    const options = e.target.options;
    const selected = [];
    for (let i = 0, len = options.length; i < len; i++) {
      if (options[i].selected) {
        selected.push(options[i].value);
      }
    }
    setSelectedCustomers(selected);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/matches', { participants: selectedCustomers });
      alert('Partida criada com sucesso!');
    } catch (error) {
      alert('Erro ao criar partida');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label>Selecionar Participantes</label>
        <select multiple className="form-control" onChange={handleSelect}>
          {customers.map(customer => (
            <option key={customer._id} value={customer._id}>
              {customer.name} ({customer.email})
            </option>
          ))}
        </select>
      </div>
      <button type="submit" className="btn btn-primary">Criar Partida</button>
    </form>
  );
};
