import React, { useState } from 'react';
import axios from 'axios';

export const WeaponForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    maintenanceDate: '',
    ammunition: '',
    purchaseDate: '',
    value: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/weapons', formData);
      alert('Arma cadastrada com sucesso!');
    } catch (error) {
      alert('Erro ao cadastrar arma');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label>Nome da Arma</label>
        <input type="text" className="form-control" name="name" onChange={handleChange} />
      </div>
      <div className="form-group">
        <label>Data de Manutenção</label>
        <input type="date" className="form-control" name="maintenanceDate" onChange={handleChange} />
      </div>
      <div className="form-group">
        <label>Tipo de Munição</label>
        <input type="text" className="form-control" name="ammunition" onChange={handleChange} />
      </div>
      <div className="form-group">
        <label>Data de Compra</label>
        <input type="date" className="form-control" name="purchaseDate" onChange={handleChange} />
      </div>
      <div className="form-group">
        <label>Valor</label>
        <input type="number" className="form-control" name="value" onChange={handleChange} />
      </div>
      <button type="submit" className="btn btn-primary">Cadastrar Arma</button>
    </form>
  );
};
